import './App.css';
import './funcs/processing.js'
import './funcs/processVars.js'
import './funcs/correlationVars.js'
import './funcs/correlation.js'
import './funcs/d3.min.js'
import './funcs/display'
import './funcs/displayVars'
import './funcs/docsUp'
import './funcs/docsUpVar'
import './funcs/downloads'
